int isList = typ.indexOf("list",0,1)>=0;

int i = typ.lastIndexOf(" ");
if (i>=0) typ = typ.substring( i+1 ) + (isList ? "[]" : "");

i = typ.lastIndexOf(":");
if (i>=0) typ = typ.substring( i+1 );

name = name.gettoken( 0, "(" );

classes::cpicks tmp = &stringS.CreateRow();
tmp.code = name.lower();
tmp.desc = typ;
tmp.PickType = curClass;

