//@cmember onCharDown
//@param int | Key |
//@param int | State |
//@param control | Ctrl |
//@version Andrew Griffin | 20-05-02 09:28 |
//@version Tim Simpson | 15-05-14 09:44 |
//@i

switch (Key) {

    case 27 :                   // Escape
/*        if (typed) {
            typed = "";
            findPossibles();
        } else*/ close();
        break;
    case 38 :                   // Up
        selectRowMove( -1, 0 );
        break;
    case 40 :                   // Down
        selectRowMove( 1, 0 );
        break;
    case 37 :                   // Left
        selectRowMove( -1, 1 );
        break;
    case 39 :                   // Right
        selectRowMove( 1, 1 );
        break;
    case 08 :                   // Back space
        if (typed) typed = typed.left( typed.length()-1 );
        findPossibles();
        break;
    case 46 :                   // Del
        typed = "";
        findPossibles();
        break;
    case 13, 32, 9, '9', 190, 186 : // Enter,Space,Tab,(,'.',':'
        if (key!='9' || State>0)
        {
            if (done==-1) {
                KillTimer();
                findPossibles2();
            }
            clicked( selY, Key );
        break;
        }
    case 189:                   //fix "_" in search
        if (state==3)
        {
            typed += "_";
        }
        findPossibles();
        break;
    default :

        string k = Key.charstring().lower();
        if (k>="a" && k<="z" || k>="0" && k<="9" || k=="½") {
            if (k=="½") k = "-";
            if (State==3) {
                if (k=="-") k="_"; else
                if (k=="8") k="*";
            }
            typed += k;
            findPossibles();
        } //else message( Key );

}

