    typedR = typedR.subString( 1 );
    isSubStr = 1;
} else isSubStr = 0;

int vis, sY = 1, tCnt, bCnt;
gridRow TitleRow = null;
gridRow baseRow = null;

for (int i=0; i<stringS.count(); i++) {
  gridrow gr = &idSub.idMember.getRow( i );

  if (stringS[i].code) {

    if (matchTyped( stringS[i].code)) {
        vis = 1;
        tCnt++;
        bCnt++;
        nofound++;
    } else {
        vis = 0;
        if (i==sY) sY++;
    }

    for (int j=0; j<2; j++)
        gr.getCell( j ).control.setVisible( vis );

  } else

  if (!stringS[i].desc) {
    if (titleRow != null)
      titleRow.getCell( 0 ).control.setVisible( tCnt>0 );

    titleRow = &gr;
    tCnt = 0;
    if (sY==i) sY++;
  } else {

    if (baseRow != null)
      baseRow.getCell( 0 ).control.setVisible( bCnt>0 );

    baseRow = &gr;
    bCnt = 0;
    if (sY==i) sY++;
  }
}

if (titleRow != null)
  titleRow.getCell( 0 ).control.setVisible( tCnt>0 );
if (baseRow != null)
  baseRow.getCell( 0 ).control.setVisible( bCnt>0 );

if (sY>=count) sY=0;

if (sY!=selY) selectRow( sY );

idSub.FitRecursive();
FitRecursive();

return noFound;

